    private void audiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_audiActionPerformed
        if (audi.isSelected()) {
            audi_modell.setEnabled(true);
            vw_modell.setEnabled(false);
        } else {
            audi_modell.setEnabled(false);
        }
    }//GEN-LAST:event_audiActionPerformed

    private void vwActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_vwActionPerformed
        if (vw.isSelected()) {
            vw_modell.setEnabled(true);
            audi_modell.setEnabled(false);
        } else {
            vw_modell.setEnabled(false);
        }
    }//GEN-LAST:event_vwActionPerformed

    private void uebernehmenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_uebernehmenActionPerformed
        if (audi.isSelected()) {

            V.aufnahmeAuto("Audi", String.valueOf(audi_modell.getSelectedItem()), Double.valueOf(preis.getText()), Integer.valueOf(zulassung.getText()));
            liste.append("Audi" + "; " + audi_modell.getSelectedItem() + "; " + zulassung.getText() + "; " + preis.getText() + "\n");
        } else {
            V.aufnahmeAuto("VW", String.valueOf(vw_modell.getSelectedItem()), Double.valueOf(preis.getText()), Integer.valueOf(zulassung.getText()));
            liste.append("VW" + "; " + vw_modell.getSelectedItem() + "; " + zulassung.getText() + "; " + preis.getText() + "\n");
        }

        preis.setText("");
        zulassung.setText("");

    }//GEN-LAST:event_uebernehmenActionPerformed

